'use strict';

var CID = require('cids');

var protons = require('protons');

var proto = protons(require('./dag.proto.js'));

var resolver = require('./resolver');

var DAGLink = require('./dag-link');

var DAGNode = require('./dag-node');

var multihashing = require('multihashing-async');

var waterfall = require('async/waterfall');

exports = module.exports;
/**
 * @callback CidCallback
 * @param {?Error} error - Error if getting the CID failed
 * @param {?CID} cid - CID if call was successful
 */

/**
 * Get the CID of the DAG-Node.
 *
 * @param {Object} dagNode - Internal representation
 * @param {Object} [options] - Options to create the CID
 * @param {number} [options.version] - CID version number. Defaults to zero if hashAlg == 'sha2-256'; otherwise, 1.
 * @param {string} [options.hashAlg] - Defaults to hashAlg for the resolver
 * @param {CidCallback} callback - Callback that handles the return value
 * @returns {void}
 */

function cid(dagNode, options, callback) {
  if (typeof options === 'function') {
    callback = options;
    options = {};
  }

  options = options || {};
  var hashAlg = options.hashAlg || resolver.defaultHashAlg;
  var version = options.version;

  if (typeof version === 'undefined') {
    version = hashAlg === 'sha2-256' ? 0 : 1;
  }

  waterfall([function (cb) {
    return serialize(dagNode, cb);
  }, function (serialized, cb) {
    return multihashing(serialized, hashAlg, cb);
  }, function (mh, cb) {
    return cb(null, new CID(version, resolver.multicodec, mh));
  }], callback);
}

function serialize(node, callback) {
  var serialized; // If the node is not an instance of a DAGNode, the link.hash might be a Base58 encoded string; decode it

  if (!DAGNode.isDAGNode(node) && node.links) {
    node.links = node.links.map(function (link) {
      return DAGLink.isDAGLink(link) ? link : DAGLink.util.createDagLinkFromB58EncodedHash(link);
    });
  }

  try {
    serialized = proto.PBNode.encode(toProtoBuf(node));
  } catch (err) {
    return callback(err);
  }

  callback(null, serialized);
}

function deserialize(data, callback) {
  var pbn = proto.PBNode.decode(data);
  var links = pbn.Links.map(function (link) {
    return new DAGLink(link.Name, link.Tsize, link.Hash);
  });
  var buf = pbn.Data == null ? Buffer.alloc(0) : Buffer.from(pbn.Data);
  DAGNode.create(buf, links, callback);
}

function toProtoBuf(node) {
  var pbn = {};

  if (node.data && node.data.length > 0) {
    pbn.Data = node.data;
  } else {
    // NOTE: this has to be null in order to match go-ipfs serialization `null !== new Buffer(0)`
    pbn.Data = null;
  }

  if (node.links && node.links.length > 0) {
    pbn.Links = node.links.map(function (link) {
      return {
        Hash: link.multihash,
        Name: link.name,
        Tsize: link.size
      };
    });
  } else {
    pbn.Links = null;
  }

  return pbn;
}

exports.serialize = serialize;
exports.deserialize = deserialize;
exports.cid = cid;
