'use strict'; // dns-nodejs gets replaced by dns-browser when webpacked/browserified

var dns = require('../runtime/dns-nodejs');

var promisify = require('promisify-es6');

module.exports = function () {
  return promisify(function (domain, opts, callback) {
    if (typeof domain !== 'string') {
      return callback(new Error('Invalid arguments, domain must be a string'));
    }

    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    opts = opts || {};
    dns(domain, opts, callback);
  });
};
