'use strict';

var promisify = require('promisify-es6');

var setImmediate = require('async/setImmediate');

var pkgversion = require('../../../package.json').version;

module.exports = function id(self) {
  return promisify(function (opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    setImmediate(function () {
      return callback(null, {
        id: self._peerInfo.id.toB58String(),
        publicKey: self._peerInfo.id.pubKey.bytes.toString('base64'),
        addresses: self._peerInfo.multiaddrs.toArray().map(function (ma) {
          return ma.toString();
        }).filter(function (ma) {
          return ma.indexOf('ipfs') >= 0;
        }).sort(),
        agentVersion: "js-ipfs/".concat(pkgversion),
        protocolVersion: '9000'
      });
    });
  });
};
