import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import {
  getProfiles,
  getActivity,
  getProfilesVerifiedAccounts,
} from '../state/actions';
import Content from '../components/Profile/Content';
import Categories from '../components/Profile/Categories';
import Nav from '../components/Nav';
import './styles/Profile.css';


class ProfilePublic extends Component {
  async componentDidMount() {
    const { location: { pathname } } = this.props;
    const profileAddress = pathname.split('/')[2];
    const profileAddressArray = new Array(profileAddress);
    this.props.getActivity(profileAddress);
    await this.props.getProfiles(profileAddressArray);

    console.log(this.props.publicProfile.proof_twitter);
    console.log(this.props.publicProfile.proof_github);
    this.props.publicProfile.proof_twitter
      && this.props.getProfilesVerifiedAccounts(this.props.publicProfile.proof_twitter);

    this.props.publicProfile.proof_github
      && this.props.getProfilesVerifiedAccounts(this.props.publicProfile.proof_github);
  }

  render() {
    return (
      <div>
        <Nav />
        <div id="profile__page">
          <div id="profile__contents">
            <Categories />
            <Content
              publicProfile={this.props.publicProfile}
              publicProfileActivity={this.props.publicProfileActivity}
            />
          </div>
        </div>
      </div>
    );
  }
}

ProfilePublic.propTypes = {
  box: PropTypes.object,
  pathname: PropTypes.object,
  location: PropTypes.object,
  publicProfile: PropTypes.object,
  publicProfileActivity: PropTypes.array,
  getProfiles: PropTypes.func,
  getActivity: PropTypes.func,
};

ProfilePublic.defaultProps = {
  box: {},
  pathname: {},
  publicProfile: {},
  publicProfileActivity: [],
  location: {},
  getProfiles: getProfiles(),
  getProfilesVerifiedAccounts: getProfilesVerifiedAccounts(),
  getActivity: getActivity(),
};

const mapState = state => ({
  box: state.threeBox.box,
  publicProfile: state.threeBox.publicProfile,
  publicProfileActivity: state.threeBox.publicProfileActivity,
});

export default withRouter(connect(mapState,
  {
    getProfiles,
    getProfilesVerifiedAccounts,
    getActivity,
  })(ProfilePublic));
