import {
  store,
} from '../state/store';

export let address =
  typeof window.web3 !== 'undefined' ? window.web3.eth.accounts[0] : ''; // eslint-disable-line no-undef

console.log('window.web3.eth.accounts[0]', window.web3.eth.accounts[0]);

const accountsPromise = new Promise((resolve, reject) => {
  window.web3.eth.getAccounts((e, accountsFound) => { // eslint-disable-line no-undef
    if (e != null) {
      reject(e);
    } else {
      console.log('accountsFound', accountsFound);
      resolve(accountsFound);
    }
  });
});

const requestAccess = () => async () => {
  const accounts = await accountsPromise;
  console.log('accounts promise', accounts[0]);
};

requestAccess();

let prevAddress;

const pollNetworkAndAddress = () => {
  setTimeout(() => {
    const currentAddress = typeof window.web3 !== 'undefined' ? window.web3.eth.accounts[0] : ''; // eslint-disable-line no-undef
    // Logged out of MM while logged in to 3Box
    if (currentAddress !== address &&
      currentAddress === undefined &&
      store.getState().threeBox.isLoggedIn) {
      prevAddress = address;
      store.dispatch({
        type: 'HANDLE_LOGGEDOUT_MODAL',
        loggedOutModal: true,
      });
      window.localStorage.setItem('userEthAddress', currentAddress);
    }

    // Logs out of 3Box
    if (currentAddress !== address &&
      currentAddress === undefined &&
      store.getState().threeBox.isLoggedIn) {
      store.dispatch({
        type: 'HANDLE_LOGGEDOUT_MODAL',
        loggedOutModal: true,
      });
      window.localStorage.setItem('userEthAddress', currentAddress);
    }

    // Switched address
    if (currentAddress !== address &&
      typeof currentAddress === 'string' &&
      currentAddress !== prevAddress &&
      address !== undefined &&
      store.getState().threeBox.isLoggedIn) {
      prevAddress = address;
      store.dispatch({
        type: 'HANDLE_SWITCHED_ADDRESS_MODAL',
        switchedAddressModal: true,
        prevAddress,
      });
      window.localStorage.setItem('userEthAddress', currentAddress);
    }

    // Switched back to previous address
    if (currentAddress !== address &&
      typeof currentAddress === 'string' &&
      typeof address === 'string' &&
      store.getState().threeBox.isLoggedIn &&
      currentAddress === prevAddress
    ) {
      store.dispatch({
        type: 'HANDLE_SWITCHED_ADDRESS_MODAL',
        switchedAddressModal: false,
        prevAddress: '',
      });
      window.localStorage.setItem('userEthAddress', currentAddress);
    }

    // Logged in to MM
    if (currentAddress !== address &&
      typeof currentAddress === 'string' &&
      address === undefined &&
      !window.ethereum) {
      store.dispatch({
        type: 'HANDLE_WALLET_LOGIN_DETECTED_MODAL',
        isSignedIntoWallet: true,
        hasWallet: true,
      });
      window.localStorage.setItem('userEthAddress', currentAddress);
    }

    // Logged out of MM while not signed in to 3Box
    if (currentAddress !== address &&
      currentAddress === undefined) {
      store.dispatch({
        type: 'HANDLE_WALLET_LOGIN_DETECTED_MODAL',
        isSignedIntoWallet: false,
        hasWallet: false,
      });
      window.localStorage.setItem('userEthAddress', currentAddress);
    }

    address = currentAddress;
    pollNetworkAndAddress();
  }, 1000);
};

pollNetworkAndAddress();

export default address;
