import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import {
  getProfiles,
} from '../state/actions';
import Content from '../components/Profile/Content';
import Categories from '../components/Profile/Categories';
import Nav from '../components/Nav';
import './styles/Profile.css';


class ProfilePublic extends Component {
  constructor(props) {
    super(props);
    this.state = {
    }
  }

  async componentDidMount() {
    const { location: { pathname } } = this.props;
    const profileAddress = new Array(pathname.substring(6));
    console.log(profileAddress);
    const publicProfile = await Box.getProfiles(profileAddress); // eslint-disable-line no-undef
    console.log(publicProfile);
  }

  render() {
    return (
      <div>
        <Nav />
        <div id="profile__page">
          <div id="profile__contents">
            <Categories />
            <Content />
          </div>
        </div>
      </div>
    )
  }
}

ProfilePublic.propTypes = {
  pathname: PropTypes.object,
  location: PropTypes.object,
  publicProfile: PropTypes.object,
  getProfiles: PropTypes.func,
};

ProfilePublic.defaultProps = {
  pathname: {},
  publicProfile: {},
  location: {},
  getProfiles: getProfiles(),
};

const mapState = state => ({
  publicProfile: state.threeBox.publicProfile,
});

export default withRouter(connect(mapState,
  {
    getProfiles,
  })(ProfilePublic));
