import {
  store,
} from '../state/store';
import {
  normalizeURL,
} from './funcs';

const {
  currentAddress,
} = store.getState().threeBox;

export const LANDING = '/';
export const PRIVACY = '/privacy';
export const TERMS = '/terms';
export const JOBS = '/jobs';
export const CREATE = '/create';
export const PROFILES = '/profiles';

// PROFILE ROUTES
export const ACTIVITY = 'activity';
export const DETAILS = 'details';
export const EDIT = 'edit';

// BASE PROFILE, UNPROTECTED
export const PUBLIC_PROFILE = '/:ethaddress';

// FORMAT STRUCTURE FOR REACT ROUTER
export const FORMAT_PROFILE_ACTIVITY = `/:ethAddress/${ACTIVITY}`;
export const FORMAT_PROFILE_ABOUT = `/:ethAddress/${DETAILS}`;
export const FORMAT_PROFILE_EDIT = `/:ethAddress/${EDIT}`;

// PROTECTED ROUTES
export const LIVE_PROFILE_ACTIVITY = currentAddress ? `/${normalizeURL(currentAddress)}/${ACTIVITY}` : '';
export const LIVE_PROFILE_ABOUT = currentAddress ? `/${normalizeURL(currentAddress)}/${DETAILS}` : '';
export const LIVE_PROFILE_EDIT = currentAddress ? `/${normalizeURL(currentAddress)}/${EDIT}` : '';
