'use strict';

var dagNodeUtil = require('./util');

var cloneLinks = dagNodeUtil.cloneLinks;
var cloneData = dagNodeUtil.cloneData;
var toDAGLink = dagNodeUtil.toDAGLink;

var DAGLink = require('../dag-link');

var DAGNode = require('./index');

var create = require('./create');

function addLink(node, link, callback) {
  var links = cloneLinks(node);
  var data = cloneData(node);

  if (DAGLink.isDAGLink(link)) {// It's a DAGLink instance
    // no need to do anything
  } else if (DAGNode.isDAGNode(link)) {
    // It's a DAGNode instance
    // convert to link
    link = toDAGLink(link);
  } else {
    // It's a Object with name, multihash/link and size
    try {
      link = new DAGLink(link.name, link.size, link.multihash || link.hash);
    } catch (err) {
      return callback(err);
    }
  }

  links.push(link);
  create(data, links, callback);
}

module.exports = addLink;
