import React, { Component } from 'react';
import { Route, Switch, withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

import * as routes from './utils/routes';
import normalizeURL from './utils/funcs';
import { store } from './state/store';
import Landing from './views/Landing';
import Profile from './views/Profile';
import ProfilePublic from './views/ProfilePublic';
import EditProfile from './views/EditProfile';
import Profiles from './views/Profiles';
import Jobs from './views/Jobs';
import Privacy from './views/Privacy';
import Terms from './views/Terms';
import Create from './views/Create';
import NavLanding from './components/NavLanding';
import Nav from './components/Nav.jsx';
import history from './history';
import './index.css';

import AppModals from './components/AppModals';

import {
  getProfileData,
  getPublicMemberSince,
  getVerifiedPublicGithub,
  getVerifiedPublicTwitter,
  getActivity,
  getBox,
  requestAccess,
  checkWeb3Wallet,
  checkNetwork,
  handleSignOut,
} from './state/actions';

import {
  handleSignInModal,
  closeErrorModal,
  handleRequireWalletLoginModal,
  handleSwitchedNetworkModal,
  handleAccessModal,
  closeRequireMetaMaskModal,
  handleConsentModal,
  handleDeniedAccessModal,
  handleLoggedOutModal,
  handleSwitchedAddressModal,
  requireMetaMaskModal,
  handleDownloadMetaMaskBanner,
  handleMobileWalletModal,
  handleOnboardingModal,
} from './state/actions-modals';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      onBoardingModalMobileOne: false,
      onBoardingModalMobileTwo: false,
      onBoardingModalMobileThree: false,
      width: window.innerWidth,
      retractNav: false,
      showSideNav: false,
    };
    this.loadData = this.loadData.bind(this);
    this.handleSignInUp = this.handleSignInUp.bind(this);
  }

  componentWillMount() {
    // window.addEventListener('resize', this.handleWindowSizeChange);
    // window.addEventListener('scroll', this.hideBar);
  }

  async componentDidMount() {
    const {
      location,
      handleDownloadMetaMaskBanner,
      handleMobileWalletModal,
      requireMetaMaskModal,
    } = this.props;

    const { pathname } = location;
    const normalizedPath = normalizeURL(pathname);

    if (typeof window.web3 === 'undefined') {
      handleDownloadMetaMaskBanner();
      handleMobileWalletModal();
    }

    if (pathname.substring(6, 8) === '0x') {
      // 1. fetch public profile
      // 2. can you openBox?
      // 3. fetch box & show signedin profile

      // await Box // eslint-disable-line no-undef
      //   .openBox(
      //     store.getState().threeBox.accountAddress || address,
      //     window.web3.currentProvider, // eslint-disable-line no-undef
      //     opts,
      //   );
    } else if (typeof window.web3 !== 'undefined'
      && (normalizedPath === routes.PROFILE
        || normalizedPath === routes.EDITPROFILE
        || normalizedPath === routes.PROFILE_ABOUT
        || normalizedPath === routes.PROFILE_ACTIVITY
        || normalizedPath === routes.PROFILE_COLLECTIBLES)) { // no wallet and lands on restricted page
      this.loadData();
    } else if (typeof window.web3 === 'undefined'
      && (normalizedPath === routes.PROFILE
        || normalizedPath === routes.EDITPROFILE
        || normalizedPath === routes.PROFILE_ABOUT
        || normalizedPath === routes.PROFILE_ACTIVITY
        || normalizedPath === routes.PROFILE_COLLECTIBLES)) { // has wallet and lands on restricted page
      history.push(routes.LANDING);
      requireMetaMaskModal();
      handleMobileWalletModal();
    }
  }

  componentWillReceiveProps(nextProps) {
    const { location } = this.props;
    const { pathname } = location;
    const normalizedPath = normalizeURL(pathname);

    // check previous route for banner behavior on /Create & /Profiles
    if (nextProps.location.pathname !== normalizedPath) {
      store.dispatch({
        type: 'PREVIOUS_ROUTE',
        previousRoute: normalizedPath,
      });
    }

    // get profile data again only when onSyncDone
    if (nextProps.onSyncFinished && nextProps.isSyncing) {
      // end onSyncDone animation
      store.dispatch({
        type: 'APP_SYNC',
        onSyncFinished: true,
        isSyncing: false,
      });

      this.loadCalls();
    }
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.handleWindowSizeChange);
    window.removeEventListener('scroll', this.hideBar);
  }

  handleWindowSizeChange = () => {
    this.setState({ width: window.innerWidth });
  };

  handleNextMobileModal = (thisModal, nextModal) => {
    this.setState({
      [`onBoardingModalMobile${thisModal}`]: false,
      [`onBoardingModalMobile${nextModal}`]: true,
    });
  }

  hideBar = () => {
    if (window.scrollY < 1) {
      this.setState({ retractNav: false });
    } else {
      this.setState({ retractNav: true });
    }
  }

  handleSideNav = () => {
    const { showSideNav } = this.state;
    this.setState({
      showSideNav: !showSideNav,
    });
  }

  loadCalls = () => {
    const {
      getActivity,
      getVerifiedPublicGithub,
      getVerifiedPublicTwitter,
      getPublicMemberSince,
      getProfileData,
    } = this.props;

    getActivity();
    getVerifiedPublicGithub();
    getVerifiedPublicTwitter();
    getPublicMemberSince();
    getProfileData('public', 'status');
    getProfileData('public', 'name');
    getProfileData('public', 'description');
    getProfileData('public', 'image');
    getProfileData('public', 'coverPhoto');
    getProfileData('public', 'location');
    getProfileData('public', 'website');
    getProfileData('public', 'employer');
    getProfileData('public', 'job');
    getProfileData('public', 'school');
    getProfileData('public', 'degree');
    getProfileData('public', 'major');
    getProfileData('public', 'year');
    getProfileData('public', 'emoji');
    getProfileData('private', 'email');
    getProfileData('private', 'birthday');
  }

  async loadData() {
    const {
      location,
      checkWeb3Wallet,
      requestAccess,
      checkNetwork,
      isSignedIntoWallet,
      isLoggedIn,
      getBox,
      showErrorModal,
      handleRequireWalletLoginModal,
      handleSignInModal,
    } = this.props;

    const { pathname } = location;
    const normalizedPath = normalizeURL(pathname);

    await checkWeb3Wallet();
    await requestAccess('directLogin');
    await checkNetwork();

    if (isSignedIntoWallet && isLoggedIn) {
      await getBox();
      if (!showErrorModal) {
        this.loadCalls();
      }
    } else if (!isSignedIntoWallet) {
      history.push(routes.LANDING);
      handleRequireWalletLoginModal();
    } else if (isSignedIntoWallet
      && !isLoggedIn
      && (normalizedPath === routes.PROFILE
        || normalizedPath === routes.EDITPROFILE
        || normalizedPath === routes.PROFILE_ABOUT
        || normalizedPath === routes.PROFILE_ACTIVITY
        || normalizedPath === routes.PROFILE_COLLECTIBLES)) {
      history.push(routes.LANDING);
      handleSignInModal();
    }
  }

  async handleSignInUp() {
    const {
      checkWeb3Wallet,
      requestAccess,
      checkNetwork,
      isSignedIntoWallet,
      getBox,
      showErrorModal,
      accessDeniedModal,
      handleRequireWalletLoginModal,
      requireMetaMaskModal,
      handleMobileWalletModal,
    } = this.props;

    if (typeof window.web3 !== 'undefined') {
      await checkWeb3Wallet();
      await requestAccess();
      await checkNetwork();

      if (isSignedIntoWallet) {
        await getBox('fromSignIn');
        if (!showErrorModal) {
          this.loadCalls();
        }
      } else if (!isSignedIntoWallet && !accessDeniedModal) {
        handleRequireWalletLoginModal();
      }
    } else if (typeof window.web3 === 'undefined') {
      requireMetaMaskModal();
      handleMobileWalletModal();
    }
  }

  render() {
    const {
      showDifferentNetworkModal,
      accessDeniedModal,
      errorMessage,
      allowAccessModal,
      alertRequireMetaMask,
      provideConsent,
      signInToWalletModal,
      signInModal,
      mobileWalletRequiredModal,
      directLogin,
      loggedOutModal,
      switchedAddressModal,
      prevNetwork,
      currentNetwork,
      onBoardingModal,
      onBoardingModalTwo,
      ifFetchingThreeBox,
      prevAddress,
      showErrorModal,
      isLoggedIn,
      isSignedIntoWallet,
      downloadBanner,
      location,
      onSyncFinished,
      isSyncing,
      hasSignedOut,
      handleRequireWalletLoginModal,
      handleSignInModal,
      handleMobileWalletModal,
      handleConsentModal,
      handleDeniedAccessModal,
      closeErrorModal,
      handleSwitchedNetworkModal,
      handleDownloadMetaMaskBanner,
      handleLoggedOutModal,
      handleSignOut,
      handleSwitchedAddressModal,
      handleOnboardingModal,
      handleAccessModal,
      closeRequireMetaMaskModal,
    } = this.props;

    const {
      onBoardingModalMobileOne,
      onBoardingModalMobileTwo,
      onBoardingModalMobileThree,
      width,
      retractNav,
      showSideNav,
    } = this.state;

    const { pathname } = location;
    const normalizedPath = normalizeURL(pathname);
    const isMobile = width <= 812; // 600
    const classHide = retractNav ? 'hide' : '';
    const mustConsentError = errorMessage && errorMessage.message && errorMessage.message.substring(0, 65) === 'Error: MetaMask Message Signature: User denied message signature.';
    const isLandingPage = pathname === routes.LANDING && 'landing';
    const { userAgent: ua } = navigator;
    const isIOS = ua.includes('iPhone');

    return (
      <div className="App">
        {(!isLoggedIn && !ifFetchingThreeBox)
          ? (
            <NavLanding
              downloadBanner={downloadBanner}
              classHide={classHide}
              handleSignInUp={this.handleSignInUp}
              handleSideNav={this.handleSideNav}
              landing={isLandingPage}
              showSideNav={showSideNav}
              pathname={normalizedPath}
            />
          )
          : (
            <Nav />
          )}

        <AppModals
          downloadBanner={downloadBanner}
          ifFetchingThreeBox={ifFetchingThreeBox}
          onSyncFinished={onSyncFinished}
          isSyncing={isSyncing}
          hasSignedOut={hasSignedOut}
          allowAccessModal={allowAccessModal}
          directLogin={directLogin}
          isMobile={isMobile}
          alertRequireMetaMask={alertRequireMetaMask}
          accessDeniedModal={accessDeniedModal}
          signInToWalletModal={signInToWalletModal}
          signInModal={signInModal}
          isIOS={isIOS}
          mobileWalletRequiredModal={mobileWalletRequiredModal}
          errorMessage={errorMessage}
          mustConsentError={mustConsentError}
          showErrorModal={showErrorModal}
          prevNetwork={prevNetwork}
          currentNetwork={currentNetwork}
          showDifferentNetworkModal={showDifferentNetworkModal}
          loggedOutModal={loggedOutModal}
          switchedAddressModal={switchedAddressModal}
          prevAddress={prevAddress}
          onBoardingModal={onBoardingModal}
          onBoardingModalTwo={onBoardingModalTwo}
          provideConsent={provideConsent}
          onBoardingModalMobileOne={onBoardingModalMobileOne}
          onBoardingModalMobileTwo={onBoardingModalMobileTwo}
          onBoardingModalMobileThree={onBoardingModalMobileThree}

          handleNextMobileModal={this.handleNextMobileModal}
          handleRequireWalletLoginModal={handleRequireWalletLoginModal}
          handleSignInModal={handleSignInModal}
          handleMobileWalletModal={handleMobileWalletModal}
          handleConsentModal={handleConsentModal}
          handleDeniedAccessModal={handleDeniedAccessModal}
          closeErrorModal={closeErrorModal}
          handleSwitchedNetworkModal={handleSwitchedNetworkModal}
          handleDownloadMetaMaskBanner={handleDownloadMetaMaskBanner}
          handleLoggedOutModal={handleLoggedOutModal}
          handleSignOut={handleSignOut}
          handleSwitchedAddressModal={handleSwitchedAddressModal}
          handleOnboardingModal={handleOnboardingModal}
          handleAccessModal={handleAccessModal}
          closeRequireMetaMaskModal={closeRequireMetaMaskModal}
        />

        <Switch>
          <Route
            exact
            path={routes.LANDING}

            render={() => (
              <Landing
                handleSignInUp={this.handleSignInUp}
                isLoggedIn={isLoggedIn}
                errorMessage={errorMessage}
                showErrorModal={showErrorModal}
                isSignedIntoWallet={isSignedIntoWallet}
              />
            )}
          />

          <Route
            path={routes.PUBLIC_PROFILE}
            component={ProfilePublic}
          />

          <Route
            path={routes.PROFILE}
            component={Profile}
          />

          <Route
            path={routes.EDITPROFILE}
            component={EditProfile}
          />

          <Route
            path={routes.JOBS}
            component={() => (
              <Jobs
                isLoggedIn={isLoggedIn}
                handleSignInUp={this.handleSignInUp}
                classHide={classHide}
              />
            )}
          />

          <Route
            path={routes.PRIVACY}
            component={() => (
              <Privacy
                isLoggedIn={isLoggedIn}
                handleSignInUp={this.handleSignInUp}
                classHide={classHide}
              />
            )}
          />

          <Route
            path={routes.TERMS}
            component={() => (
              <Terms
                isLoggedIn={isLoggedIn}
                handleSignInUp={this.handleSignInUp}
                classHide={classHide}
              />
            )}
          />

          <Route
            path={routes.CREATE}
            component={() => (
              <Create
                isLoggedIn={isLoggedIn}
                handleSignInUp={this.handleSignInUp}
                classHide={classHide}
              />
            )}
          />

          <Route
            path={routes.PROFILES}
            component={() => (
              <Profiles
                isLoggedIn={isLoggedIn}
                handleSignInUp={this.handleSignInUp}
                classHide={classHide}
              />
            )}
          />

        </Switch>

      </div>
    );
  }
}

