'use strict';

var PeerId = require('peer-id');

var PeerInfo = require('peer-info');

var multiaddr = require('multiaddr');

var setImmediate = require('async/setImmediate');

module.exports = function (node) {
  /*
   * Helper method to check the data type of peer and convert it to PeerInfo
   */
  return function (peer, callback) {
    var p; // PeerInfo

    if (PeerInfo.isPeerInfo(peer)) {
      p = peer; // Multiaddr instance or Multiaddr String
    } else if (multiaddr.isMultiaddr(peer) || typeof peer === 'string') {
      if (typeof peer === 'string') {
        try {
          peer = multiaddr(peer);
        } catch (err) {
          return setImmediate(function () {
            return callback(err);
          });
        }
      }

      var peerIdB58Str = peer.getPeerId();

      if (!peerIdB58Str) {
        return setImmediate(function () {
          callback(new Error('peer multiaddr instance or string must include peerId'));
        });
      }

      try {
        p = node.peerBook.get(peerIdB58Str);
      } catch (err) {
        p = new PeerInfo(PeerId.createFromB58String(peerIdB58Str));
      }

      p.multiaddrs.add(peer); // PeerId
    } else if (PeerId.isPeerId(peer)) {
      var _peerIdB58Str = peer.toB58String();

      try {
        p = node.peerBook.get(_peerIdB58Str);
      } catch (err) {
        return node.peerRouting.findPeer(peer, callback);
      }
    } else {
      return setImmediate(function () {
        return callback(new Error('peer type not recognized'));
      });
    }

    setImmediate(function () {
      return callback(null, p);
    });
  };
};
