import { withRouter } from 'react-router-dom';
import React, { Component } from 'react';

import * as routes from '../utils/routes';
import Content from '../components/Profile/Content';
import Categories from '../components/Profile/Categories';
import Nav from '../components/Nav';
import './styles/Profile.css';

class Profile extends Component {
  constructor(props) {
    super(props);
    const { location } = this.props;
    const { pathname } = location;
    this.state = {
      isPublicProfile: !!(pathname.split('/')[1] === routes.PUBLIC_BASE.substring(1)),
    };
  }

  componentDidMount() {
    window.scrollTo(0, 0);
  }

  render() {
    const { isPublicProfile } = this.state;
    return (
      <div>
        <Nav />
        <div id="profile__page">
          <div id="profile__contents">
            <Categories isPublicProfilePage={isPublicProfile} />
            <Content isPublicProfilePage={isPublicProfile} />
          </div>
        </div>
      </div>
    );
  }
}

Profile.propTypes = {
  location: PropTypes.object.isRequired,
};

export default withRouter(Profile);
