'use strict';

var pkg = require('../../../package.json');

var promisify = require('promisify-es6'); // TODO add the commit hash of the current ipfs version to the response.


module.exports = function version(self) {
  return promisify(function (opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    self.repo.version(function (err, repoVersion) {
      if (err) {
        return callback(err);
      }

      callback(null, {
        version: pkg.version,
        repo: repoVersion,
        commit: ''
      });
    });
  });
};
