'use strict';

var setImmediate = require('async/setImmediate');

var NOT_STARTED_YET = require('./error-messages').NOT_STARTED_YET;

var FloodSub = require('libp2p-floodsub');

module.exports = function (node) {
  var floodSub = new FloodSub(node);
  node._floodSub = floodSub;
  return {
    subscribe: function subscribe(topic, options, handler, callback) {
      if (typeof options === 'function') {
        callback = handler;
        handler = options;
        options = {};
      }

      if (!node.isStarted() && !floodSub.started) {
        return setImmediate(function () {
          return callback(new Error(NOT_STARTED_YET));
        });
      }

      function subscribe(cb) {
        if (floodSub.listenerCount(topic) === 0) {
          floodSub.subscribe(topic);
        }

        floodSub.on(topic, handler);
        setImmediate(cb);
      }

      subscribe(callback);
    },
    unsubscribe: function unsubscribe(topic, handler) {
      if (!node.isStarted() && !floodSub.started) {
        throw new Error(NOT_STARTED_YET);
      }

      floodSub.removeListener(topic, handler);

      if (floodSub.listenerCount(topic) === 0) {
        floodSub.unsubscribe(topic);
      }
    },
    publish: function publish(topic, data, callback) {
      if (!node.isStarted() && !floodSub.started) {
        return setImmediate(function () {
          return callback(new Error(NOT_STARTED_YET));
        });
      }

      if (!Buffer.isBuffer(data)) {
        return setImmediate(function () {
          return callback(new Error('data must be a Buffer'));
        });
      }

      floodSub.publish(topic, data);
      setImmediate(function () {
        return callback();
      });
    },
    ls: function ls(callback) {
      if (!node.isStarted() && !floodSub.started) {
        return setImmediate(function () {
          return callback(new Error(NOT_STARTED_YET));
        });
      }

      var subscriptions = Array.from(floodSub.subscriptions);
      setImmediate(function () {
        return callback(null, subscriptions);
      });
    },
    peers: function peers(topic, callback) {
      if (!node.isStarted() && !floodSub.started) {
        return setImmediate(function () {
          return callback(new Error(NOT_STARTED_YET));
        });
      }

      if (typeof topic === 'function') {
        callback = topic;
        topic = null;
      }

      var peers = Array.from(floodSub.peers.values()).filter(function (peer) {
        return topic ? peer.topics.has(topic) : true;
      }).map(function (peer) {
        return peer.info.id.toB58String();
      });
      setImmediate(function () {
        return callback(null, peers);
      });
    },
    setMaxListeners: function setMaxListeners(n) {
      return floodSub.setMaxListeners(n);
    }
  };
};
