'use strict';

var CID = require('cids');

var pull = require('pull-stream');

module.exports = function (cid, node, name, path, pathRest, resolve, size, dag, parent, depth) {
  var newNode;

  if (pathRest.length) {
    var pathElem = pathRest[0];
    newNode = node[pathElem];
    var newName = path + '/' + pathElem;

    if (!newNode) {
      return pull.error(new Error("not found"));
    }

    var isCID = CID.isCID(newNode);
    return pull(pull.values([{
      depth: depth,
      name: pathElem,
      path: newName,
      pathRest: pathRest.slice(1),
      multihash: isCID && newNode,
      object: !isCID && newNode,
      parent: parent
    }]), resolve);
  } else {
    return pull.error(new Error('invalid node type'));
  }
};
