import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import './styles/Create.css'

class ProfilePublic extends Component {
  constructor(props) {
    super(props);
    this.state = {
      disableSave: true,
    };
  }

  handleUpdatePic = (photoFile, e, cover) => {
    const type = cover ? 'coverPhoto' : 'image';

    if (photoFile.size <= 2500000) {
      const formData = new window.FormData();
      formData.append('path', photoFile);
      this.setState({ disableSave: false });

      if (cover) {
        this.setState({
          editCoverPic: true, coverBuffer: formData, removeCoverPic: false,
        });
      } else {
        this.setState({
          editPic: true, buffer: formData, removeUserPic: false,
        });
      }
    } else {
      e.target.value = null;
      this.setState({ showFileSizeModal: true });
    }
  }

  render() {
    const { disableSave } = this.state;

    return (
      <div className="createPage">
        <div className="create__guide">
          <h2>Start your meme</h2>
          <p>Begin by uploading an image you think would make for a great beginning of a meme.</p>
        </div>
        <div className="progress__wrapper">
          <div className="progress__steps">
            <p>Start</p>
            <p>Draw</p>
            <p>Caption</p>
            <p>Sell</p>
          </div>

          <div className="progress__bar__wrapper">
            <div className="progress__bar" />
            <div className="progress__bar grey" />
            <div className="progress__bar grey" />
            <div className="progress__bar grey" />
          </div>
        </div>

        <div className="canvas__wrapper">
          <div className="canvas">
            <label htmlFor="coverInput" id="chooseCanvas">
              <input
                id="coverInput"
                type="file"
                name="coverPic"
                className="light"
                accept="image/*"
                onChange={e => this.handleUpdatePic(e.target.files[0], e, true)}
                ref={ref => this.coverUpload = ref}
              />
              <div className="edit__profile__editCanvas__button">
                Edit
                    </div>
            </label>
          </div>
        </div>

        <button
          type="submit"
          disabled={disableSave}
        >
          Save
        </button>

      </div>
    );
  }
}

ProfilePublic.propTypes = {
  getProfile: PropTypes.func.isRequired,
  getActivity: PropTypes.func.isRequired,
  handleSignInBanner: PropTypes.func.isRequired,
  pathname: PropTypes.object,
  location: PropTypes.object,
  isLoadingPublicProfile: PropTypes.bool,
  showSignInBanner: PropTypes.bool,
  currentAddress: PropTypes.string,
};

ProfilePublic.defaultProps = {
  pathname: {},
  location: {},
  isLoadingPublicProfile: true,
  showSignInBanner: false,
  currentAddress: '',
};

const mapState = state => ({
  // currentAddress: state.threeBox.currentAddress,
});

export default withRouter(connect(mapState,
  {
    // getProfile,
    // getActivity,
    // handleSignInBanner,
  })(ProfilePublic));
